INSERT INTO rpg_questions (type, content, answer_key, difficulty, subject_tag)
VALUES
('mcq', '{"prompt":"Approximately what period does Baroque music span?","choices":["c. 1400 – c. 1600","c. 1600 – c. 1750","c. 1750 – c. 1810","c. 1810 – c. 1900"],"explanation":"The Baroque period is roughly dated from 1600 to 1750."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Which of these is a defining feature of Baroque music, providing a harmonic foundation with an improvised chordal part over a bass line?","choices":["Sonata Form","Basso Continuo","Leitmotif","Atonality"],"explanation":"Basso Continuo, played by a bass instrument and a chordal instrument, is a hallmark of Baroque music."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What is the term for sudden shifts in volume (e.g., loud to soft) common in Baroque music?","choices":["Crescendo","Diminuendo","Terraced Dynamics","Sforzando"],"explanation":"Terraced dynamics refer to abrupt changes in volume levels, rather than gradual ones."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"The death of which composer in 1750 is often cited by music historians as marking the end of the Baroque era?","choices":["Claudio Monteverdi","Antonio Vivaldi","Johann Sebastian Bach","George Frideric Handel"],"explanation":"J.S. Bach''s death in 1750 is a common marker for the end of the Baroque period."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Which of the following is NOT considered a major genre/form of the Baroque era?","choices":["Opera","Concerto Grosso","Fugue","Symphony"],"explanation":"The Symphony as a major form developed and rose to prominence primarily in the Classical period, following the Baroque."}'::jsonb, '{"correct":3}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"The ''Doctrine of Affections'' (Affektenlehre) in Baroque music refers to:","choices":["A system of tuning instruments.","The rules for composing complex polyphony.","The belief that music could and should arouse specific emotions in the listener.","The practice of improvising ornaments."],"explanation":"The Doctrine of Affections was the idea that music should evoke specific, singular emotions or states of mind."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"A Concerto Grosso typically features:","choices":["A single solo instrument and orchestra.","A small group of soloists (concertino) and a larger orchestra (ripieno).","Only string instruments.","A choir and orchestra."],"explanation":"The Concerto Grosso contrasts a small group of soloists (concertino) with the larger orchestral ensemble (ripieno or tutti)."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"The term ''Baroque'' originally comes from a word meaning:","choices":["Highly logical and structured","An irregularly shaped pearl","Simple and elegant","Ancient and revered"],"explanation":"The word ''Baroque'' comes from the Portuguese ''barroco,'' meaning an irregularly shaped pearl, initially describing ornate art."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Which of these major historical developments was NOT a key part of the Baroque era''s context?","choices":["The Scientific Revolution (e.g., Newton, Galileo)","The rise of absolute monarchies (e.g., Louis XIV)","The invention of the printing press","Religious conflicts like the Thirty Years'' War"],"explanation":"The invention of the printing press (Gutenberg) occurred in the 15th century, well before the Baroque era, though its impact continued."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Which composer, who worked under Louis XIV, was known for developing the French overture and ''tragédie en musique''?","choices":["Arcangelo Corelli","Henry Purcell","Jean-Baptiste Lully","Domenico Scarlatti"],"explanation":"Jean-Baptiste Lully was a dominant figure in French Baroque music, crucial in developing French opera and the French overture."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What was Vivaldi''s famous nickname, related to his hair color?","choices":["The Golden Maestro","The Red Priest","The Venetian Virtuoso","Father Antonio"],"explanation":"Vivaldi was known as ''Il Prete Rosso'' (The Red Priest) because of his red hair."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Where did Vivaldi work for many years, teaching music to orphaned girls?","choices":["St. Mark''s Basilica","The Royal Academy of Music","Ospedale della Pietà","La Scala Opera House"],"explanation":"Vivaldi taught and composed for the Ospedale della Pietà, a home and music school for orphaned girls in Venice."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Vivaldi is especially famous for composing over 500 pieces in which genre?","choices":["Operas","Symphonies","String Quartets","Concertos"],"explanation":"Vivaldi was a prolific composer of concertos, with over 500 to his name, mostly for the violin."}'::jsonb, '{"correct":3}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What is the collective name of Vivaldi''s most famous set of violin concertos that depict seasons?","choices":["The Venetian Masters","The Grand Tour","The Four Seasons","Baroque Highlights"],"explanation":"His best-known work is ''The Four Seasons,'' a set of four violin concertos each representing a season."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What happened to Vivaldi''s music for a long time after his death?","choices":["It became even more popular immediately.","It was performed only in Italy.","It was largely forgotten.","It was banned by the Church."],"explanation":"After his death, Vivaldi''s music was largely forgotten until its rediscovery in the 20th century."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Antonio Vivaldi was a composer from which country?","choices":["Germany","France","Italy","England"],"explanation":"Vivaldi was born in Venice, Italy, and is a celebrated Italian composer."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"In what year was Antonio Vivaldi born?","choices":["1650","1678","1701","1725"],"explanation":"Vivaldi''s life spanned from 1678 to 1741."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"In what year did Antonio Vivaldi die?","choices":["1720","1741","1750","1765"],"explanation":"Vivaldi passed away in 1741."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Vivaldi is a prominent composer of which musical period?","choices":["Renaissance","Baroque","Classical","Romantic"],"explanation":"Vivaldi''s music is a prime example of the Baroque style, known for being fancy and energetic."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What does Vivaldi''s nickname ''Il Prete Rosso'' translate to in English?","choices":["The Fast Composer","The Violin King","The Red Priest","The Musical Father"],"explanation":"''Il Prete Rosso'' means ''The Red Priest,'' a nickname Vivaldi received due to his red hair."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Besides being a renowned composer, Vivaldi was also known as a:","choices":["Painter and sculptor","Poet and playwright","Priest and violinist","Scientist and inventor"],"explanation":"Vivaldi was an ordained priest and a virtuoso violinist in addition to his composing career."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"The Ospedale della Pietà, where Vivaldi worked, was a home and music school for:","choices":["Young noblemen","Gifted boys from wealthy families","Orphaned girls","Traveling musicians"],"explanation":"Vivaldi dedicated many years to teaching music to orphaned girls at the Ospedale della Pietà."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Why was Vivaldi''s work at the Ospedale della Pietà significant for his compositions?","choices":["It provided him with a large salary.","It allowed him to travel extensively.","It gave him a chance to experiment with new musical ideas.","It was a very relaxed job with few duties."],"explanation":"The Ospedale provided Vivaldi with an environment to try out new musical forms and styles with the talented student orchestra."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Approximately how many concertos did Vivaldi compose?","choices":["Around 50","Around 100","Around 250","Over 500"],"explanation":"Vivaldi was incredibly prolific, composing over 500 concertos, making them his specialty."}'::jsonb, '{"correct":3}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"For which instrument did Vivaldi write the majority of his concertos?","choices":["Flute","Cello","Violin","Oboe"],"explanation":"Most of Vivaldi''s concertos were written for the violin, an instrument he played masterfully."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Besides the violin, Vivaldi also wrote concertos for which of these instruments (mentioned in the text)?","choices":["Trumpet","Timpani","Bassoon","Harp"],"explanation":"Vivaldi''s concertos featured various solo instruments, including the bassoon, cello, oboe, and flute."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Vivaldi composed many works in a genre where stories are told entirely through singing. What is this genre?","choices":["Sonatas","Symphonies","Operas","Cantatas"],"explanation":"Vivaldi wrote many operas, which are plays where the dialogue is sung."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"As a priest, Vivaldi composed sacred music for the church. Which of these is a famous sacred work by him mentioned in the text?","choices":["Messiah","St. Matthew Passion","Gloria","The Creation"],"explanation":"His sacred music includes the well-known ''Gloria''."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Which of these best describes a common characteristic of Vivaldi''s musical style regarding rhythm?","choices":["Slow and melancholic","Gentle and flowing","Energetic and driving","Simple and repetitive"],"explanation":"Vivaldi''s music is often characterized by strong, driving rhythms."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What is often said about the melodies in Vivaldi''s music?","choices":["They are usually complex and hard to follow.","They are often somber and dark.","They are clear and memorable.","They are mostly improvised."],"explanation":"His melodies are typically clear, bright, and memorable."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Vivaldi''s ''The Four Seasons'' is part of a larger collection of twelve concertos. What is this collection called?","choices":["L''estro armonico","Il cimento dell''armonia e dell''inventione","La stravaganza","Concerti grossi, Op. 3"],"explanation":"''The Four Seasons'' forms the first four concertos of ''Il cimento dell''armonia e dell''inventione'' (The Contest Between Harmony and Invention), Op. 8."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What type of music, which aims to describe a story or scene, are ''The Four Seasons'' concertos prime examples of?","choices":["Absolute Music","Chamber Music","Programme Music","Sacred Music"],"explanation":"They are excellent examples of programme music, as each concerto depicts a season and its associated imagery."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What did Vivaldi provide with each concerto in ''The Four Seasons'' to help listeners understand the musical depictions?","choices":["Detailed paintings","A short story","Sonnets (poems)","Performance instructions for dancers"],"explanation":"Vivaldi provided a sonnet for each season, with specific lines often marked in the score corresponding to musical events."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What is the typical number of movements in each concerto of ''The Four Seasons'', and what is their tempo pattern?","choices":["Four movements (Slow-Fast-Slow-Fast)","Three movements (Fast-Slow-Fast)","Two movements (Fast-Slow)","Five movements (Fast-Slow-Fast-Slow-Fast)"],"explanation":"Each concerto typically follows a three-movement structure: Fast-Slow-Fast."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What is the solo instrument featured prominently in ''The Four Seasons''?","choices":["Cello","Flute","Violin","Oboe"],"explanation":"The violin is the solo instrument, showcasing Vivaldi''s own virtuosity on the instrument."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"The accompanying orchestra in ''The Four Seasons'' consists of a string orchestra and what other essential Baroque component?","choices":["A brass section","A percussion section","Basso continuo","A choir"],"explanation":"The string orchestra is supported by the basso continuo, typically a harpsichord/organ and a cello/bassoon."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Which season does ''La Primavera'' represent?","choices":["Summer","Autumn","Winter","Spring"],"explanation":"''La Primavera'' is Italian for Spring. It is the first concerto in the set."}'::jsonb, '{"correct":3}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What is the home key of Vivaldi''s ''Spring'' concerto?","choices":["G major","E minor","C major","E major"],"explanation":"The ''Spring'' concerto is in the bright and cheerful key of E major."}'::jsonb, '{"correct":3}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"In the first movement (Allegro) of ''Spring'', what does the opening ritornello theme represent?","choices":["A thunderstorm","The song of birds","The arrival of Spring","A murmuring stream"],"explanation":"The energetic opening ritornello joyfully announces the arrival of Spring."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"How does Vivaldi musically depict birdsong in ''Spring'', Movement 1?","choices":["Low, rumbling notes in the cellos","Trills and high-pitched passages in the solo violins","A slow, mournful melody in the violas","Loud chords from the full orchestra"],"explanation":"Birdsong is imitated by solo violins using trills, rapid repeated notes, and high, decorative figures."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What musical technique does Vivaldi use to represent murmuring streams in ''Spring'', Movement 1?","choices":["Sharp, staccato chords","Gentle, flowing melodies in the strings","A trumpet fanfare","Silence"],"explanation":"Murmuring streams are depicted through smooth, flowing melodic lines in the string section."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"How is a spring thunderstorm musically portrayed in ''Spring'', Movement 1?","choices":["Quiet, pizzicato strings","A gentle flute solo","Tremolos, rushing scales, and a shift to a minor key","A joyful dance rhythm"],"explanation":"The thunderstorm is created with tremolos (rapidly repeated notes), fast scales, and often a shift to a minor key to build drama."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What happens in the music after the thunderstorm passes in ''Spring'', Movement 1?","choices":["The music ends abruptly.","A slow, sad theme is introduced.","The birdsong returns, followed by the main ritornello theme.","The tempo becomes much slower."],"explanation":"After the storm, the cheerful birdsong motifs return, and the sunny ritornello theme is heard again."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"The first movement of ''Spring'' is a good example of which common Baroque musical form, where a main theme alternates with solo episodes?","choices":["Fugue","Sonata Form","Ritornello Form","Theme and Variations"],"explanation":"Ritornello form, with its alternating orchestral ritornello and solo episodes, is characteristic of this movement."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"In ritornello form, what is the term for the main orchestral theme that returns?","choices":["Episode","Coda","Ritornello","Development"],"explanation":"The recurring main theme played by the full orchestra is called the ritornello."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What is the Italian term for the full orchestra in a Baroque concerto, contrasting with the soloist(s)?","choices":["Solo","Concertino","Ripieno (or Tutti)","Continuo"],"explanation":"The full orchestra is referred to as the ripieno or tutti."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What does the tempo marking ''Allegro'' indicate for the first movement of ''Spring''?","choices":["Slow and expressive","Moderately fast","Fast and lively","Very slow"],"explanation":"Allegro is an Italian tempo marking meaning fast, quick, and bright."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"The sonnets accompanying ''The Four Seasons'' help to clarify the music''s:","choices":["Harmonic structure","Programmatic content","Rhythmic complexity","Instrumentation details"],"explanation":"The sonnets detail the scenes and events that the music aims to portray, clarifying its programmatic nature."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Which two instruments typically form the core of the basso continuo?","choices":["Violin and Viola","Flute and Oboe","Harpsichord/Organ and Cello/Bassoon","Trumpet and Timpani"],"explanation":"The basso continuo is typically realized by a keyboard instrument (harpsichord or organ) playing chords over a bass line played by a low string or wind instrument (cello or bassoon)."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"The contrasting sections between the full orchestra and the solo violin in ''Spring'' Mvt. 1 are a hallmark of which musical genre?","choices":["Symphony","Opera Aria","Baroque Concerto","Fugue"],"explanation":"The contrast between soloist(s) and the orchestra (tutti/ripieno) is a fundamental characteristic of the Baroque concerto."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque');
INSERT INTO rpg_questions (type, content, answer_key, difficulty, subject_tag)
VALUES
('mcq', '{"prompt":"In Handel''s Concerto Grosso Op. 6 No. 5, what is the typical instrumentation of the ''concertino'' (solo group)?","choices":["Flute, oboe, and bassoon","Two violins and a cello","Solo harpsichord","Trumpet, horn, and timpani"],"explanation":"The concertino in Handel''s Op. 6 (following Corelli''s model) typically consists of two violins and a cello."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What is the key of Handel''s Concerto Grosso Op. 6, No. 5?","choices":["G major","D minor","D major","B minor"],"explanation":"Handel''s Concerto Grosso Op. 6, No. 5 is in the bright and festive key of D major."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"The first movement (Larghetto e staccato) of Op. 6 No. 5 is reminiscent of what type of Baroque instrumental piece, known for its majestic dotted rhythms?","choices":["Gigue","Fugue","French Overture (opening section)","Chaconne"],"explanation":"The dotted rhythms and stately character of the first movement are characteristic of the slow opening section of a French Overture."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What is the primary musical form of the second movement (Allegro) of Op. 6 No. 5?","choices":["Ritornello Form","Binary Form","Fugue","Theme and Variations"],"explanation":"The second movement is a lively and energetic fugue, showcasing polyphonic texture."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"The fourth movement (Largo) of Op. 6 No. 5 is in which key, providing a contrast to the home key of D major?","choices":["D major","G major","E minor","B minor"],"explanation":"The slow and expressive fourth movement is in B minor, the relative minor of D major, creating a poignant contrast."}'::jsonb, '{"correct":3}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Which dance form is represented by the sixth movement (Menuet) of Op. 6 No. 5?","choices":["Sarabande","Gigue","Minuet","Courante"],"explanation":"The sixth movement is a graceful Minuet, a common Baroque dance in triple meter."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"Handel''s Concerti Grossi Op. 6 were heavily influenced by the model of which earlier Italian Baroque composer?","choices":["Antonio Vivaldi","Domenico Scarlatti","Claudio Monteverdi","Arcangelo Corelli"],"explanation":"Handel''s Op. 6 follows the Corellian model for the Concerto Grosso, particularly in the concertino/ripieno setup."}'::jsonb, '{"correct":3}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"The term ''ripieno'' in a Concerto Grosso refers to:","choices":["The solo cello part only.","The small group of soloists.","The main orchestral body (tutti).","The harpsichord player."],"explanation":"The ripieno (or tutti) is the larger orchestral group that contrasts with the smaller concertino."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"How many movements does Handel''s Concerto Grosso Op. 6 No. 5 have, which is unusual for the Op. 6 set?","choices":["Three","Four","Five","Six"],"explanation":"This concerto is unusual for having six movements, giving it a suite-like character."}'::jsonb, '{"correct":3}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"What does the tempo marking ''Larghetto e staccato'' for the first movement suggest about its character?","choices":["Very fast and smooth","Fairly slow but with crisp, detached notes","Moderately slow and heavily accented","Extremely slow and mournful"],"explanation":"''Larghetto'' means fairly slow, and ''e staccato'' indicates a detached, crisp articulation, creating a stately yet articulated character."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"TRUE or FALSE: Handel''s Concerto Grosso Op. 6 No. 5 was composed during his time in London.","choices":["True","False"],"explanation":"True. Handel composed the Op. 6 Concerti Grossi in 1739 while living and working in London."}'::jsonb, '{"correct":0}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"TRUE or FALSE: The concerto grosso form was invented by Handel.","choices":["True","False"],"explanation":"False. The concerto grosso form was developed by Italian composers, particularly Arcangelo Corelli, before Handel adopted and refined it."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"ODD ONE OUT: Which of these is NOT a typical movement in Handel''s Concerto Grosso Op. 6 No. 5?","choices":["Larghetto e staccato","Allegro (Fugue)","Rondo Allegro","Menuet"],"explanation":"Rondo Allegro is not a movement in this concerto. The actual movements include Larghetto e staccato, Allegro (Fugue), Presto, Largo, Allegro, and Menuet."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"ODD ONE OUT: Which instrument would NOT typically be found in the ripieno (tutti) of a Baroque concerto grosso?","choices":["Violin","Viola","Piano","Harpsichord"],"explanation":"Piano would not be found in a Baroque concerto grosso as it was not invented until the Classical period. The harpsichord was the keyboard instrument of choice."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"ORDERING: Arrange these movements of Op. 6 No. 5 in their correct sequence (1st to 4th):","choices":["Larghetto e staccato → Allegro → Presto → Largo","Allegro → Larghetto e staccato → Largo → Presto","Presto → Largo → Larghetto e staccato → Allegro","Largo → Presto → Allegro → Larghetto e staccato"],"explanation":"The correct order of the first four movements is: 1. Larghetto e staccato, 2. Allegro, 3. Presto, 4. Largo."}'::jsonb, '{"correct":0}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"SEQUENCE: In the development of the concerto grosso form, arrange these composers chronologically:","choices":["Corelli → Vivaldi → Handel → Bach","Handel → Corelli → Bach → Vivaldi","Bach → Vivaldi → Corelli → Handel","Vivaldi → Handel → Corelli → Bach"],"explanation":"Chronologically: Corelli (1653-1713) established the form, Vivaldi (1678-1741) developed it, Handel (1685-1759) and Bach (1685-1750) were contemporaries who perfected it."}'::jsonb, '{"correct":0}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"MATCHING: Which tempo marking correctly matches the character description ''Stately and majestic with dotted rhythms''?","choices":["Presto","Larghetto e staccato","Allegro","Menuet"],"explanation":"Larghetto e staccato creates a stately, majestic character with its moderate slow tempo and detached articulation, often featuring dotted rhythms."}'::jsonb, '{"correct":1}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"MATCHING: Which key relationship correctly pairs with ''Relative minor of D major''?","choices":["F# minor","A major","B minor","G major"],"explanation":"B minor is the relative minor of D major, sharing the same key signature (two sharps: F# and C#)."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"SELECT ALL: Which of these are characteristic features of the Baroque concerto grosso? (Choose the MOST comprehensive answer)","choices":["Only: Contrast between solo and tutti sections","Only: Use of basso continuo","Only: Ritornello form in fast movements","All of the above: Contrast between solo/tutti, basso continuo, and ritornello form"],"explanation":"All three features are characteristic of the Baroque concerto grosso: contrast between concertino and ripieno, basso continuo accompaniment, and ritornello form structure."}'::jsonb, '{"correct":3}'::jsonb, 1, 'baroque'),
('mcq', '{"prompt":"SELECT ALL: Which instruments would typically be found in Handel''s concertino group? (Choose the COMPLETE set)","choices":["Only: Two violins","Only: Cello","Two violins and cello","Two violins, cello, and harpsichord"],"explanation":"The concertino in Handel''s Op. 6 typically consists of two violins and a cello, following the Corellian model."}'::jsonb, '{"correct":2}'::jsonb, 1, 'baroque'),
