('mcq', '{"prompt":"Which of these is a ''Mixed Choir''?","choices":["Only men.","Only children.","Men and women (SATB).","Only robots."],"explanation":"A mixed choir combines both male and female voices (SATB)."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Indeterminate or chance music is also known as:","choices":["Strophic.","Aleatoric.","Monodic.","Romantic."],"explanation":"Aleatoric music (from Latin ''alea'' meaning dice) leaves some elements to chance or performer choice."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The Classical period in music roughly spans which set of years?","choices":["c. 1600 – c. 1750","c. 1750 – c. 1820","c. 1820 – c. 1900","c. 1400 – c. 1600"],"explanation":"The Classical period is generally considered to be from around 1750 to 1820, following the Baroque and preceding the Romantic era."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which musical texture became predominant in the Classical era, emphasizing a clear melody with accompaniment?","choices":["Polyphonic","Monophonic","Homophonic","Heterophonic"],"explanation":"Homophonic texture, with a clear melody and chordal accompaniment, was the predominant texture in Classical music, contrasting with Baroque polyphony."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which of these composers is considered one of the main figures of the ''First Viennese School'' and the Classical era?","choices":["J.S. Bach","G.F. Handel","Wolfgang Amadeus Mozart","Franz Liszt"],"explanation":"Wolfgang Amadeus Mozart, along with Haydn and early Beethoven, is a key composer of the First Viennese School and the Classical style."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is the name of the most important structural design of the Classical era, typically used for the first movement of sonatas, symphonies, and string quartets?","choices":["Ritornello Form","Fugue","Rondo Form","Sonata Form"],"explanation":"Sonata Form, with its Exposition, Development, and Recapitulation sections, was the cornerstone of many Classical first movements."}'::jsonb, '{"correct":3}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Compared to the Baroque ''terraced dynamics'', what type of dynamic changes became much more common and expressive in the Classical era?","choices":["Only sudden loud accents (sforzando).","Consistently soft dynamics.","Gradual changes like crescendo and diminuendo.","No dynamic variation at all."],"explanation":"Gradual dynamic changes (crescendo and diminuendo) were a key expressive tool in Classical music, facilitated by instruments like the fortepiano."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which term describes a common Classical accompaniment pattern consisting of broken chords played by the left hand on a keyboard instrument?","choices":["Basso Continuo","Alberti Bass","Ground Bass","Ostinato"],"explanation":"The Alberti Bass, a pattern of broken chords (e.g., lowest note, highest, middle, highest), was a frequent accompaniment texture in Classical keyboard music."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Classical melodies are often described as:","choices":["Highly ornamented and complex.","Short, fragmented, and rhythmically irregular.","Clear, balanced, memorable, and organized in symmetrical phrases.","Based primarily on folk songs."],"explanation":"Clarity, balance, and memorable, singable (cantabile) qualities are hallmarks of Classical melodies, often structured in clear phrases."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The Classical orchestra became more standardized. Which instrument largely disappeared from orchestral use as its harmonic function was written into instrumental parts?","choices":["Violin","Flute","Timpani","Harpsichord (as a continuo instrument)"],"explanation":"The Basso Continuo, typically played by a harpsichord and a bass instrument, largely disappeared as harmonies became fully notated within the orchestral parts."}'::jsonb, '{"correct":3}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"How many original compositions must a candidate submit for Component 3?","choices":["One","Two","Three","Four"],"explanation":"Candidates must submit two contrasting compositions."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What notation requirement is mandatory for Composition 1?","choices":["Graphic notation","Staff notation","Chord symbols only","No notation is required"],"explanation":"The first composition must be submitted in full staff notation."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is the recommended duration for each composition?","choices":["Under 1 minute","1 to 3 minutes","4 to 6 minutes","Over 5 minutes"],"explanation":"Each composition should typically be between 1 and 3 minutes in length."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"For Composition 2, which of these can be submitted instead of a staff notation score?","choices":["A drawing of the music","A written commentary","A video of the composer","A list of instruments used"],"explanation":"For Composition 2, a written commentary is acceptable if traditional notation is not appropriate for the style."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What does \"Development\" in the marking criteria refer to?","choices":["The age of the student","How musical ideas are transformed, extended, or varied","The volume of the recording","The software used to write the music"],"explanation":"Development is the process of taking initial ideas (motifs) and evolving them through the piece."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"True or False: An arrangement of a pop song is acceptable as a composition.","choices":["True","False"],"explanation":"Compositions must be original. Arrangements of existing music are not permitted."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which marking category assesses the creative use of instruments or voices?","choices":["Structure","Use of Medium","Compositional Techniques","Notation"],"explanation":"\"Use of Medium\" refers to how effectively the composer writes for their chosen instruments or voices."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"How many marks is each composition worth?","choices":["25 marks","50 marks","100 marks","10 marks"],"explanation":"Each composition is marked out of 50, making a total of 100 for Component 3."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What must accompany EVERY composition submission?","choices":["A photo of the score","A recording of the piece","A letter from the parent","A biography of the composer"],"explanation":"Both a written version (score/commentary) and a recording are required for all compositions."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"candidate-defined brief\"?","choices":["An exam paper given by Cambridge","A student-chosen purpose or stimulus for the music","A list of rules for the classroom","A summary of the syllabus"],"explanation":"Composition 2 requires the student to define a \"brief\" or scenario for their music."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In the marking criteria, what does \"Texture\" refer to?","choices":["The feel of the score paper","The layers of sound and how they interact","The speed of the music","The volume of the music"],"explanation":"Texture describes how different melodic and harmonic lines are layered (e.g., homophonic, polyphonic)."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which is a common method for creating high-quality recordings?","choices":["Humming into a phone","Using music software (DAW) and virtual instruments","Recording in a noisy hallway","There is no need for high quality"],"explanation":"Using Digital Audio Workstations (DAWs) is a professional way to produce clear recordings for submission."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What determines the mark for \"Notation and Presentation\"?","choices":["The color of the ink","How clearly and accurately the score communicates the composer’s intent","The number of pages","The font size"],"explanation":"Clear notation helps the moderator understand exactly what the composer intended to happen."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Can a composition use electronic or synthesized sounds?","choices":["Yes, if appropriate to the style","No, only acoustic instruments are allowed"],"explanation":"Electronic sounds are perfectly acceptable, especially in styles like EDM or film music."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What does \"Contrasting\" mean in the context of the two compositions?","choices":["They must have different titles","They should use different styles, instruments, or musical periods","They should be by different people","One must be loud and one must be soft"],"explanation":"Contrast shows the composer’s range; submitting two very similar pieces is discouraged."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"Motif\"?","choices":["A long 10-minute melody","A short, recurring musical idea or fragment","A type of instrument","The ending of a piece"],"explanation":"A motif is the \"seed\" from which much of a composition is developed."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"True or False: The teacher must certify that the work is original.","choices":["True","False"],"explanation":"Authentication is critical to ensure the student actually wrote the music themselves."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which marking section evaluates the overall plan and shape of the music?","choices":["Melody","Structure","Harmony","Rhythm"],"explanation":"Structure refers to the formal organization (e.g., Binary, Ternary, Rondo) of the piece."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"How does a commentary differ from a score?","choices":["A score is words, a commentary is notes","A score is a visual map of the music; a commentary explains the \"why\" and \"how\" in words","There is no difference","A commentary is only for the teacher"],"explanation":"Commentaries are used when standard notation is not the primary way the music was created (e.g., studio production)."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Harmonic Language\"?","choices":["The language people speak in Germany","The choice and use of chords and tonality in the piece","The way the melody is sung","The speed of the harmony"],"explanation":"Harmonic language refers to the composer''s approach to chords, scales, and keys."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What does \"EDM\" stand for?","choices":["Electronic Dance Music","Electric Drum Machine","Ensemble Digital Mix","Extended Dance Mode"],"explanation":"EDM stands for Electronic Dance Music, a broad genre for electronic-based club music."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"DAW\"?","choices":["Dance Audio Wave","Digital Audio Workstation","Dynamic Audio Widget","Direct Audio Writing"],"explanation":"A DAW is software used for recording, editing, and producing music (e.g., Ableton, Logic)."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a typical tempo range for House music?","choices":["70-90 BPM","118-130 BPM","160-180 BPM","200+ BPM"],"explanation":"House music typically ranges from 118-130 BPM."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is the characteristic \"four-on-the-floor\" beat?","choices":["A syncopated pattern","A kick drum on every beat (1, 2, 3, 4)","No drums at all","Only hi-hats"],"explanation":"Four-on-the-floor is a steady kick drum on every quarter note."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"Drop\" in EDM?","choices":["When the music stops","The main climax where the bass and beat hit hard","The intro","A vocal solo"],"explanation":"The Drop is the high-energy climax after a buildup."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"Build-up\" in EDM?","choices":["The end of the song","A section of increasing tension before the Drop","A slow ballad section","The bassline"],"explanation":"Build-ups use rising pitch, snare rolls, and effects to create anticipation."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Dubstep\" known for?","choices":["Acoustic guitars","Heavy bass wobbles and half-time rhythms","Orchestral arrangements","Country vocals"],"explanation":"Dubstep features aggressive basslines and a slower, half-time drum feel around 140 BPM."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"Synthesizer\"?","choices":["An acoustic instrument","An electronic instrument that generates sound electronically","A type of speaker","A microphone"],"explanation":"Synthesizers create sounds using oscillators, filters, and envelopes."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Sampling\"?","choices":["Recording live instruments","Taking a portion of existing audio and reusing it in a new track","Only using original sounds","Singing"],"explanation":"Sampling involves taking pieces of existing recordings and using them creatively."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"Loop\"?","choices":["A one-time sound","A repeating section of audio or MIDI","A vocal effect","The end of a song"],"explanation":"Loops are repeating patterns that form the backbone of electronic tracks."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What genre typically has tempos around 170+ BPM?","choices":["Ambient","Drum and Bass","Deep House","Lo-fi hip hop"],"explanation":"Drum and Bass is a fast genre, typically 165-180 BPM."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Sidechain Compression\" used for in EDM?","choices":["Making vocals louder","Creating a pumping effect where the bass ducks under the kick","Adding reverb","Tuning instruments"],"explanation":"Sidechaining creates the characteristic pumping effect in EDM."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"MIDI\"?","choices":["Musical Instrument Digital Interface (a data protocol)","A type of audio file","A synthesizer brand","A genre of music"],"explanation":"MIDI is a protocol for sending musical data (notes, velocity, etc.) between devices."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"Filter\" in synthesis?","choices":["A device that cuts or boosts certain frequencies","A type of speaker","A microphone","A reverb effect"],"explanation":"Filters shape sound by removing or emphasizing specific frequencies."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What genre originated in Detroit in the 1980s?","choices":["Dubstep","Techno","Trance","Hardstyle"],"explanation":"Detroit Techno was pioneered by Juan Atkins, Derrick May, and Kevin Saunderson."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is an \"Arpeggiator\"?","choices":["A type of drum","A tool that plays notes of a chord in sequence","A vocal effect","A bass guitar"],"explanation":"Arpeggiators automatically cycle through held chord notes in patterns."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Reverb\"?","choices":["A distortion effect","Simulated room acoustics/echoes","A type of bass","A vocal style"],"explanation":"Reverb simulates the natural reflections of sound in a space."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"Riser\" sound?","choices":["A bass sound","A sound effect that increases in pitch to build tension","A type of snare","A vocal sample"],"explanation":"Risers are ascending sounds used in build-ups before drops."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Automation\" in a DAW?","choices":["Recording changes to parameters over time","Playing instruments live","A type of effect","A mixing console"],"explanation":"Automation records dynamic changes (volume, filter cutoff, etc.) over time."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Mastering\"?","choices":["Recording vocals","The final stage of audio processing for distribution","Writing lyrics","Setting up microphones"],"explanation":"Mastering is the final polish to make a track loud, clear, and consistent."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general');
INSERT INTO rpg_questions (type, content, answer_key, difficulty, subject_tag)
VALUES
('mcq', '{"prompt":"What is a \"Leitmotif\" in film music?","choices":["A loud noise","A recurring musical theme associated with a specific character, place, or idea","The credits music","A pop song"],"explanation":"Leitmotifs (like Darth Vader''s theme or the Jaws theme) help tell the story musically."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Underscore\"?","choices":["Music played softly under dialogue","Music played during the credits","Source music","A song sung by a character"],"explanation":"Underscore provides atmosphere and emotion without overpowering the spoken words."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Diegetic\" (Source) music?","choices":["Background music the characters can''t hear","Music that exists within the film''s world (e.g., from a radio) that characters CAN hear","Scary music","Orchestral music"],"explanation":"If a character turns on a radio, that music is diegetic."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What does \"Non-Diegetic\" music mean?","choices":["Music characters can hear","Background score added for the audience, unheard by characters","Sound effects","Dialogue"],"explanation":"Most film scores are non-diegetic; they comment on the action rather than being part of it."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which composer is known for \"Star Wars\", \"Jaws\", and \"Harry Potter\"?","choices":["Hans Zimmer","John Williams","Ennio Morricone","Danny Elfman"],"explanation":"John Williams is the most famous symphonic film composer of the modern era."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Mickey-Mousing\"?","choices":["Using cartoon voices","When the music mimics every physical action on screen","Playing happy music","Using silence"],"explanation":"Common in cartoons (and early films), this technique tightly synchronizes music to movement."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"Hit Point\"?","choices":["A precise moment where music must coincide with a visual event","A loud drum","The end of the film","A wrong note"],"explanation":"Composers and editors use hit points to sync accents (like a punch or explosion) with the score."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Hans Zimmer is famous for combining orchestra with...","choices":["Only choir","Electronic synthesizers and modern production","Bluegrass bands","Solo piano only"],"explanation":"Zimmer pioneered the hybrid orchestral-electronic sound (e.g., Inception, Dark Knight)."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Dissonance\" used for in film?","choices":["Romance scenes","Creating tension, fear, or suspense","Comedy","End credits"],"explanation":"Clashing notes (dissonance) naturally create unease, perfect for horror or thrillers."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"Pedal Note\"?","choices":["A sustained low note that builds tension","A high note","A fast scale","A drum solo"],"explanation":"A long pedal note acts as an anchor that can build suspense or stability."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Bernard Herrmann''s score for \"Psycho\" is famous for using only...","choices":["Brass","Strings (string orchestra)","Synthesizers","Vocals"],"explanation":"Herrmann used a stark, black-and-white sound palate of strings only (hello, shower scene screech!)."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Ostinato\"?","choices":["A short, repeated musical pattern","A long melody","A silence","A crash"],"explanation":"Ostinatos are crucial for driving action scenes or building suspense."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What mood does a \"Major Key\" usually convey?","choices":["Sadness and fear","Happiness, heroism, or safety","Anger","Mystery"],"explanation":"Culturally, major keys are associated with brightness and positivity."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Silence\" used for in film?","choices":["Boredom","To create extreme tension or focus on dialogue/sound design","To save money","Mistake"],"explanation":"Sudden silence can be louder than a full orchestra, shocking the audience."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Danny Elfman is often associated with the films of...","choices":["Steven Spielberg","Tim Burton","George Lucas","James Cameron"],"explanation":"Elfman''s quirky, dark, and magical style fits perfectly with Tim Burton''s visuals."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Minimalism\" in film music?","choices":["Using very few instruments or repetitive patterns","Playing very quietly","Short films only","No music"],"explanation":"Minimalist scores (like Phillip Glass) use repetition and subtle changes to create hypnotic atmospheres."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Instrumentation\"?","choices":["The choice of instruments used to create a specific timbre","Repairing instruments","Buying instruments","Tuning"],"explanation":"Choosing a saxophone vs. a cello drastically changes the \"color\" and meaning of a scene."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Ennio Morricone is famous for scoring specifically which genre?","choices":["Sci-Fi","Spaghetti Westerns","Rom-Coms","Documentaries"],"explanation":"Morricone gave the Western its iconic sound (whistles, electric guitars, choirs)."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"Main Title\" cue?","choices":["The music played during the opening credits/scenes","The music at the end","Music for the villain","Sound effects"],"explanation":"The Main Title establishes the tone, themes, and \"brand\" of the movie immediately."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Why might a composer use a \"Tremolo\" on strings?","choices":["To sound funny","To create suspense, shivering, or tension","To sound like a drum","To signify sleep"],"explanation":"Rapidly repeating notes (tremolo) creates a sense of vibrating tension."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Adaptive (Dynamic) Audio\"?","choices":["Music that stays the same","Music that changes in real-time based on the player''s actions","Audio played on speakers","Recorded dialogue"],"explanation":"Adaptive audio reacts to gameplay states (e.g., combat starting, health low)."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Chiptune\" (8-bit) music?","choices":["Music made with real orchestras","Music synthesized by early computer sound chips","Music about chips","Vocal music"],"explanation":"Chiptune relies on simple waveforms (square, triangle, noise) due to early hardware limitations."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Looping\"?","choices":["Playing a track specifically once","Seamlessly repeating a piece of music indefinitely","Stopping the music","Skipping tracks"],"explanation":"Game music must loop because we don''t know how long a player will stay in one area."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Horizontal Resequencing\"?","choices":["Playing songs backwards","Switching between different musical sections (e.g., Explore -> Combat) as the music flows","Layering instruments","Turning up the volume"],"explanation":"Horizontal resequencing seamlessly transitions between different \"blocks\" of music along the timeline."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Vertical Layering\"?","choices":["Adding or removing instrument tracks to change intensity","Switching songs","Dancing vertically","Playing simple melodies"],"explanation":"Vertical layering keeps the same musical flow but adds layers (e.g., drums enter) when action heats up."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Who composed the iconic \"Super Mario Bros.\" theme?","choices":["Nobuo Uematsu","Koji Kondo","Jeremy Soule","Hans Zimmer"],"explanation":"Koji Kondo is the legendary composer behind Nintendo''s Mario and Zelda franchises."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"Leitmotif\" in games?","choices":["A text box","A recurring theme for a character (like Zelda''s Lullaby)","A sound effect","A glitch"],"explanation":"Just like in film, leitmotifs help players instantly recognize characters or places."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Foley\" in games?","choices":["Background music","Recorded everyday sound effects (footsteps, cloth rustle)","Voice acting","Coding"],"explanation":"Foley artists record real-world sounds to make the game environment feel realistic."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What limitation defined early game music (NES/GameBoy)?","choices":["It had to be classical","Limited number of sound channels (polyphony)","It had to be 10 minutes long","No limitations"],"explanation":"Early consoles could often only play 3 or 4 notes at once."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"MIDI\" in the context of old PC games?","choices":["Recorded audio","Instructions for the computer''s sound card to play notes","A video format","A controller"],"explanation":"MIDI files were tiny because they were just instructions; the user''s sound card generated the actual audio."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which game is famous for its Gregorian Chant-style title theme?","choices":["Tetris","Halo","Sonic the Hedgehog","Pac-Man"],"explanation":"Halo''s chant theme became iconic for its ancient, mysterious atmosphere."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Immersion\"?","choices":["Playing underwater","The feeling of being completely absorbed in the game world","Winning the game","Turning off the sound"],"explanation":"Audio (music and SFX) is critical for suspending disbelief and creating immersion."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"Stinger\"?","choices":["A very short musical sound to signal an event (e.g., getting a coin)","A long song","A wrong note","A bee"],"explanation":"Stingers provide instant positive or negative feedback to the player."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Modern AAA games often use...","choices":["Only beeps","Full live orchestral scores","Silence only","Mono audio"],"explanation":"Big budget games now rival Hollywood films with massive orchestral productions."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Procedural Audio\"?","choices":["Recorded songs","Sound generated in real-time by algorithms (e.g., wind, footsteps)","Radio music","Voice acting"],"explanation":"Procedural audio creates variety, so footsteps don''t sound exactly the same every time."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Nobuo Uematsu is famous for the music of which RPG series?","choices":["Call of Duty","Final Fantasy","FIFA","Tetris"],"explanation":"Uematsu''s emotional scores are central to the identity of Final Fantasy."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What defines \"Open World\" music?","choices":["Fast techno","Sparse, ambient music that allows for long exploration without annoyance","Constant loud singing","No music"],"explanation":"Games like Skyrim or Breath of the Wild use ambient music to avoid repetitive fatigue."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Spatial Audio\" (or 3D Audio)?","choices":["Loud volume","Sound that simulates direction and distance (surround sound)","Flat sound","Mono sound"],"explanation":"Spatial audio allows players to hear if an enemy is behind or above them (crucial for VR/Shooters)."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Why do boss battles usually have unique music?","choices":["They don''t","To heighten intensity and signal a major challenge","Because the boss sings","To annoy the player"],"explanation":"High-octane boss themes raise the stakes and adrenaline."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The \"Tetris\" theme is actually a folk song from which country?","choices":["USA","Russia","Japan","Gemany"],"explanation":"The theme is based on the Russian folk song \"Korobeiniki.\""}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is the Italian tempo marking for this piece?","choices":["Adagio","Moderato","Allegro","Presto"],"explanation":null}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is the indicated beats per minute (BPM) for the quarter note?","choices":["120","110","90","105"],"explanation":null}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Based on the key signature, what is the primary key of this concerto?","choices":["G Major","A Major","D Major","Major D) B Minor"],"explanation":null}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which Baroque group of instruments is represented by \"V3\" (Basso Continuo)?","choices":["Concertino","Ripieno","oncertino B) Ripieno C) Woodwind Section","Brass Fanfare"],"explanation":null}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In Bars 1–2, the Cembalo (V2) enters one bar after the Violino (V1) with the same melody. What is this texture called?","choices":["Monophonic","Homophonic","Imitative Polyphony","Heterophonic"],"explanation":null}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
