('mcq', '{"prompt":"What is an ''Ensemble'' number in a musical?","choices":["A song for the whole cast.","A song for one person.","A song with no instruments.","The overture."],"explanation":"Ensembles involve most or all of the cast, often for high-energy climaxes."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Modern pop ballads often use which instrument as the primary focus?","choices":["Tuba.","Piano or Acoustic Guitar.","Bagpipes.","Synthetically generated white noise."],"explanation":"Ballads typically rely on piano or acoustic guitar to support the vocal intimacy."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The ''Chorus'' of a pop song is usually:","choices":["The quietest part.","The most repetitive and highest-energy section.","The intro.","Spoken, not sung."],"explanation":"The chorus contains the ''payoff'' or hook and is designed for maximum impact."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which of these evolved from Opera and is a direct ancestor of the modern musical?","choices":["Heavy Metal.","Operetta/Vaudeville.","Folk Song.","Chant."],"explanation":"Operettas combined singing and dialogue, leading directly to the birth of ''Musicals''."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is ''Reverb'' in a song?","choices":["The singer repeating a word.","An electronic effect that simulates a sense of space/echo.","Stopping the song.","Tuning the voice."],"explanation":"Reverb is essential for modern vocal production to add depth and ''vibe''."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which term describes a song where the melody is very speech-like?","choices":["Recitative-style/Parlando.","Bel Canto.","Coloratura.","Drone."],"explanation":"Parlando or recitative-style singing prioritizes natural speech rhythms over melody."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"A ''Pre-Chorus'' section usually:","choices":["Is at the end of the song.","Builds tension between the Verse and the Chorus.","Is an instrumental solo.","Has the same lyrics as the verse."],"explanation":"The pre-chorus acts as a ''ramp'' to launch the listener into the main hook."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In musical theatre, the ''Overture'' is:","choices":["The song during the intermission.","A medley of the show''s themes played by the orchestra at the start.","When the lead actor bows.","A type of vocal warm-up."],"explanation":"The overture sets the mood and introduces many of the melodies the audience will hear later."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is ''Syncopation''?","choices":["Singing in perfect time.","Accenting the off-beats or weak parts of the beat.","Having a lot of instruments.","Using only natural instruments."],"explanation":"Syncopation is vital for the ''groove'' in pop, jazz, and rock music."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What does the Italian word ''ritornello'' literally mean?","choices":["A grand opening","A little return","A solo display","A lively dance"],"explanation":"''Ritornello'' means ''little return,'' referring to the recurring main theme."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In Ritornello Form, which section is typically played by the full orchestra (tutti)?","choices":["Episode","Coda","Ritornello","Development"],"explanation":"The Ritornello section features the full orchestra (tutti)."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which section in Ritornello Form often features the soloist(s) and is modulatory?","choices":["Ritornello","Episode","Introduction","Bridge"],"explanation":"Episodes are typically performed by the soloist(s), are often virtuosic, and frequently modulate to different keys."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In which musical era was Ritornello Form most prominently used, especially in concertos?","choices":["Renaissance","Baroque","Classical","Romantic"],"explanation":"Ritornello Form was a defining structure for the fast movements of Baroque concertos."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"How do later Ritornello sections often differ from the opening Ritornello?","choices":["They are always played slower.","They are always longer and more complex.","They may be abbreviated or in different keys.","They are played only by the soloists."],"explanation":"Later Ritornello statements are often shortened and can appear in related keys, though the final ritornello is usually in the tonic."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is the typical harmonic function of the first Ritornello in a movement?","choices":["To modulate to the dominant key immediately.","To establish the tonic key firmly.","To be tonally ambiguous.","To end in the relative minor."],"explanation":"The opening Ritornello typically establishes the main themes and the tonic key of the movement."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What kind of musical material is commonly found in the Episode sections?","choices":["Only exact repetitions of the ritornello theme by the soloist.","Virtuosic passages, new thematic ideas, or development of ritornello motives.","Slow, lyrical melodies exclusively.","Strictly contrapuntal fugal sections."],"explanation":"Episodes provide contrast and often feature soloistic display, new themes, or development of existing material."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In Vivaldi''s ''Spring,'' Mvt 1, what does the first Episode primarily depict?","choices":["Murmuring streams","A thunderstorm","Birdsong","The arrival of Spring"],"explanation":"The first episode in ''Spring,'' Mvt 1, uses solo violins with trills and high-pitched passages to depict birdsong."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"How does Vivaldi represent ''Murmuring Streams'' in an episode of ''Spring,'' Mvt 1?","choices":["Loud brass fanfares.","Gentle, flowing semiquaver patterns in the strings.","Aggressive staccato chords.","A solo harpsichord passage."],"explanation":"Vivaldi uses gentle, flowing figures, often with semiquavers in the strings, to evoke the sound of murmuring streams."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which musical elements are used to depict the ''Thunder and Lightning'' episode in ''Spring,'' Mvt 1?","choices":["Quiet pizzicato and a slow tempo.","A pastoral oboe solo.","String tremolos, rapid scales, and a shift to a minor key.","A joyful dance rhythm."],"explanation":"The storm is depicted with dramatic string tremolos, fast scales, and often a move to a minor key."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The main Ritornello theme in ''Spring,'' Mvt 1, is associated with what programmatic idea?","choices":["The chill of winter","The joyful arrival of Spring","The hunt","A peasant dance"],"explanation":"The opening and recurring main Ritornello theme signifies the cheerful arrival of Spring."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In Vivaldi''s ''Spring,'' Mvt. 1, what happens to the key during the thunderstorm episode?","choices":["It stays firmly in E major.","It often shifts to a minor key.","It modulates to G major.","It becomes atonal."],"explanation":"To enhance the drama of the thunderstorm, Vivaldi often shifts the music to a minor key during this episode."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"How many main Ritornello sections are typically identified in analyses of Vivaldi''s ''Spring,'' Mvt. 1?","choices":["Two","Three","Four","Five (or more, depending on how fragments are counted)"],"explanation":"Common analyses identify around five main Ritornello statements, though some may be fragmented or abbreviated."}'::jsonb, '{"correct":3}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is the primary solo instrument in Vivaldi''s ''Spring'' concerto?","choices":["Flute","Violin","Cello","Harpsichord"],"explanation":"''Spring'' is a violin concerto, featuring the violin as the main solo instrument."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"J.S. Bach''s Brandenburg Concerto No. 5, 1st Mvt, is a famous example of Ritornello Form. Which instrument has a particularly prominent and extended solo episode (cadenza)?","choices":["Violin","Flute","Harpsichord","Cello"],"explanation":"The harpsichord has an exceptionally virtuosic and lengthy solo cadenza in the first movement of Brandenburg Concerto No. 5."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What type of aria, common in Baroque opera, often features instrumental ritornellos at the beginning, end, and sometimes between vocal sections?","choices":["Strophic Aria","Aria da Capo","Recitative Accompagnato","Arioso"],"explanation":"The Da Capo aria (A-B-A form) typically incorporates instrumental ritornellos."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In the Classical era, Ritornello principles often blended with which other major structural form in the first movements of concertos?","choices":["Fugue","Rondo Form","Theme and Variations","Sonata Form"],"explanation":"Classical concerto first movements often combine elements of Ritornello Form (tutti/solo alternation, returning themes) with Sonata Form (exposition, development, recapitulation)."}'::jsonb, '{"correct":3}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Mozart''s Piano Concerto No. 20 in D minor, K. 466, 1st Mvt, begins with an orchestral section that presents main themes before the piano enters. This is analogous to what part of Ritornello Form?","choices":["A solo episode","The opening Ritornello","A coda","A cadenza"],"explanation":"The orchestral exposition in a Classical concerto serves a similar function to the opening Ritornello, establishing key thematic material."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which of these popular music structures is most analogous to the alternating nature of Ritornello Form?","choices":["Through-composed form","12-bar blues","Verse-Chorus form","Binary form"],"explanation":"Verse-Chorus form, with its recurring chorus (like a ritornello) and contrasting verses (like episodes), shares a conceptual similarity."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In popular music, what might be considered analogous to a short, recurring Ritornello?","choices":["A complex guitar solo","The bridge section","A catchy instrumental riff or hook","The lyrics of the first verse"],"explanation":"A memorable, recurring instrumental riff or hook can function in a way similar to a concise ritornello, providing a point of return."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The term ''concertino'' in a Concerto Grosso refers to:","choices":["The full orchestra.","The small group of soloists.","The main recurring theme.","The slow middle movement."],"explanation":"The concertino is the small group of solo instruments that contrasts with the ripieno (full orchestra) in a Concerto Grosso."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is the typical dynamic characteristic of a Ritornello section compared to an Episode featuring a solo flute?","choices":["Ritornello is softer, Episode is louder.","Both are equally loud.","Ritornello is generally louder (tutti), Episode is generally softer (solo).","Dynamics are always gradually changing."],"explanation":"The Ritornello, played by the full orchestra (tutti), is typically louder than an Episode featuring a solo instrument like a flute, exemplifying terraced dynamics."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Igor Stravinsky''s ''Concerto for Piano and Wind Instruments'' is an example of:","choices":["A Romantic era symphony.","A Baroque opera overture.","A Neoclassical work influenced by Baroque concerto ideas.","A Classical sonata for piano."],"explanation":"Stravinsky''s work is from his Neoclassical period, drawing inspiration from Baroque forms and principles, including ritornello-like structures."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In Vivaldi''s ''Spring,'' the use of specific musical figures to represent birds, streams, and storms is an example of:","choices":["Absolute music.","Serialism.","Programme music (or word painting in instrumental music).","Folk music transcription."],"explanation":"This is a hallmark of programme music, where the music aims to depict extra-musical scenes or ideas."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The structural principle of Ritornello Form is primarily based on:","choices":["Continuous development of a single theme.","Alternation between a recurring tutti section and contrasting solo sections.","A strict set of variations on a ground bass.","The imitation of a subject by multiple voices as in a fugue."],"explanation":"The core idea of Ritornello Form is the alternation between the recurring Ritornello (tutti) and the contrasting Episodes (soloist/s)."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which composer, known for his Concerti Grossi Op. 6, was a major influence on Handel''s approach to the genre?","choices":["Johann Sebastian Bach","Antonio Vivaldi","Arcangelo Corelli","Domenico Scarlatti"],"explanation":"Arcangelo Corelli''s Concerti Grossi, Op. 6, served as important models for later composers, including Handel."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In the context of Ritornello Form, the Episodes are crucial for providing:","choices":["Tonal stability and thematic repetition.","Harmonic and thematic contrast, and soloistic display.","A slow, introspective mood only.","A simplified version of the Ritornello theme."],"explanation":"Episodes offer contrast to the Ritornello in terms of themes, harmony (often modulatory), and instrumentation (highlighting soloists)."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Thematic development or fragmentation of Ritornello motives is more likely to occur in:","choices":["The Ritornello sections.","The Episode sections.","The Coda only.","The Basso Continuo part exclusively."],"explanation":"While Ritornellos present themes, the Episodes are where these themes might be developed, fragmented, or where new related material is introduced by the soloist(s)."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"A key characteristic of the Ritornello theme itself is that it is often:","choices":["Vague and improvisatory.","Played very quietly by a solo instrument.","Bold, memorable, and clearly defined.","Extremely long and complex, avoiding repetition."],"explanation":"The Ritornello theme is usually strong, memorable, and well-defined, making it recognizable upon its returns."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In Vivaldi''s ''Spring,'' the return of birdsong after the thunderstorm episode provides:","choices":["An increase in dramatic tension.","A modulation to a distant minor key.","A sense of relief and return to the initial mood.","The main climax of the movement."],"explanation":"The return of the birdsong after the storm offers a sense of relief and a return to the more joyful, pastoral atmosphere of Spring."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The tonal plan of a Ritornello Form movement typically involves the opening and closing Ritornellos in the ____ key.","choices":["Dominant","Subdominant","Tonic","Relative minor"],"explanation":"The first and final Ritornello statements are usually in the tonic key, providing a sense of tonal grounding and closure."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which of these is NOT a primary function of the Ritornello section?","choices":["To present main thematic material.","To provide tonal stability.","To primarily showcase solo virtuosity.","To serve as a structural pillar."],"explanation":"Solo virtuosity is primarily showcased in the Episode sections, not the tutti Ritornello sections."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The French Overture, with its slow-fast-slow structure, often uses instrumental ritornellos in its faster, fugal section. Which Baroque composer perfected this form?","choices":["Antonio Vivaldi","Jean-Baptiste Lully","Domenico Scarlatti","Arcangelo Corelli"],"explanation":"Jean-Baptiste Lully is credited with perfecting the French Overture form at the court of Louis XIV."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"A common feature of Episode sections, related to harmony, is that they are often:","choices":["Statically in the tonic key.","Modulatory, exploring different keys.","Exclusively using parallel harmony.","Atonal and dissonant."],"explanation":"Episodes frequently modulate, moving away from the key of the preceding ritornello and preparing for the next."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In Vivaldi''s ''Spring,'' the depiction of the ''murmuring streams'' episode primarily features which section of the orchestra?","choices":["Brass and timpani","Strings (often violins)","Solo woodwinds","The Basso Continuo alone"],"explanation":"The ''murmuring streams'' are depicted by gentle, flowing semiquaver patterns predominantly in the string section."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The concept of a ''double exposition'' in Classical concertos means:","choices":["The orchestra plays the exposition twice before the soloist enters.","The soloist plays the main themes first, then the orchestra repeats them.","The orchestra presents an exposition, followed by the soloist presenting their version of the exposition (often with new material).","There are two development sections."],"explanation":"In Double Exposition Form, the orchestra first presents the main themes (orchestral exposition), then the soloist enters and presents their own exposition, often elaborating or adding new themes."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general');
INSERT INTO rpg_questions (type, content, answer_key, difficulty, subject_tag)
VALUES
('mcq', '{"prompt":"The Neoclassical movement in the 20th century saw composers like Stravinsky:","choices":["Completely rejecting all past musical forms.","Drawing inspiration from Classical and Baroque forms and techniques, reinterpreting them in a modern style.","Focusing exclusively on electronic music.","Writing music primarily in the Romantic tradition."],"explanation":"Neoclassicism involved a return to the clarity, forms, and techniques of earlier periods like the Baroque and Classical, but infused with modern harmonies and rhythms."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What role does the Basso Continuo typically play during a Ritornello section?","choices":["It is usually silent.","It provides the main melodic material.","It provides the harmonic foundation and rhythmic drive along with the orchestra.","It plays a virtuosic solo part."],"explanation":"The Basso Continuo is an integral part of the tutti orchestra in Ritornello sections, providing harmonic support and rhythmic impetus."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"True or False: An Episode in Ritornello Form must always introduce entirely new thematic material.","choices":["True","False"],"explanation":"False. Episodes can introduce new material, but they can also develop or fragment motives from the Ritornello, or consist of virtuosic passages."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The return of the full Ritornello theme at the end of a movement provides a strong sense of:","choices":["Ambiguity and openness.","Tension and instability.","Unity and closure.","Sudden surprise."],"explanation":"The final statement of the Ritornello, typically in the tonic key, brings the movement to a satisfying close and unifies the structure."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The ''concertato principle'', fundamental to the Baroque concerto and Ritornello Form, emphasizes:","choices":["Uniformity of texture and dynamics.","The contrast and interplay between different musical forces (e.g., solo vs. tutti).","The exclusion of all ornamentation.","A very slow and solemn tempo throughout."],"explanation":"The concertato principle is all about contrast and dialogue between different groups of instruments or voices, or between soloist(s) and orchestra."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In Vivaldi''s ''Spring,'' the shift to C-sharp minor for the thunderstorm episode serves to create a stark contrast in:","choices":["Tempo only.","Instrumentation only.","Mood and tonality.","Rhythmic complexity only."],"explanation":"The move to the relative minor key of C-sharp minor dramatically changes the mood to reflect the dark and turbulent thunderstorm."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"A ''Cadenza'' in a concerto, often occurring near the end of a movement, is typically:","choices":["A quiet tutti section played by the orchestra.","An improvised or written-out virtuosic solo passage for the soloist.","A repeat of the opening Ritornello.","A section for Basso Continuo alone."],"explanation":"A cadenza is a passage for the soloist to display their technical skill, often improvised in the Baroque and Classical eras, and typically occurring before the final Ritornello or closing section."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The concept of the ''tutti'' in Ritornello form refers to passages played by:","choices":["Only the solo instruments.","The entire orchestral ensemble.","Only the continuo section.","A pair of violins."],"explanation":"''Tutti'' (Italian for ''all'') refers to sections played by the full orchestra."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What quality of Baroque music is highlighted by the frequent use of sequence in episode sections of Ritornello Form?","choices":["Its static nature.","Its preference for very short, fragmented ideas.","Its drive and forward momentum, often built through repetition at different pitch levels.","Its avoidance of clear melodic lines."],"explanation":"Sequences (repeating a melodic or harmonic pattern at a higher or lower pitch) contribute to the sense of drive, development, and forward motion common in Baroque music, especially in episodes."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In many Baroque concertos, the slow second movement often provides a contrast to the outer fast movements by being more:","choices":["Rhythmically complex and aggressive.","Lyrical, expressive, and song-like (cantabile).","Fugal and contrapuntal.","Dominated by loud brass fanfares."],"explanation":"The slow middle movement of a Baroque concerto is typically more lyrical and expressive, offering a contrast to the energy of the outer movements."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The underlying harmonic structure of Ritornello Form relies heavily on the clear establishment of:","choices":["Atonality.","Modal ambiguity.","The tonic key and its relationships with closely related keys.","Complex polyrhythms."],"explanation":"Ritornello Form is firmly rooted in the major-minor tonal system, with the tonic key as a central pillar and modulations to closely related keys providing harmonic interest and structure."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"When a Ritornello section returns in a key other than the tonic, it often signals:","choices":["The end of the movement.","A point of tonal arrival after a modulatory episode.","A complete breakdown of the form.","That the soloists will now play the Ritornello."],"explanation":"Ritornello statements in related keys (like the dominant or relative minor) often mark significant tonal arrival points within the movement, usually following a modulatory episode."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The technique of ''abbreviation'' in later Ritornello statements means that:","choices":["The Ritornello is played much faster.","Only a portion or key motive of the original Ritornello is used.","The Ritornello is played by fewer instruments.","The Ritornello is always played with added ornamentation."],"explanation":"Later returns of the Ritornello, especially those in different keys or within the body of the movement, are often shortened, using only a recognizable part of the original theme."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which of the following best describes the overall effect of Ritornello Form in a Baroque concerto movement?","choices":["A sense of continuous, seamless flow without clear sections.","A highly unpredictable and chaotic structure.","A balance of unity (through returning Ritornellos) and variety (through contrasting Episodes).","A structure dominated entirely by the solo instrument."],"explanation":"Ritornello Form effectively balances unity, provided by the recurring main theme, with variety, offered by the contrasting material and keys of the solo episodes."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is the approximate date range of the Romantic period in music?","choices":["1600-1750","1750-1820","1810-1900","1900-present"],"explanation":"The Romantic period roughly spans from 1810 to 1900, following the Classical era."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which of these is a CORE characteristic of Romantic music?","choices":["Musical restraint and balance","Emotional expression and individualism","Strict adherence to formal structures","Small, chamber-sized orchestras only"],"explanation":"Romantic composers prioritized emotion, imagination, and personal expression over Classical restraint."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What term describes music that seeks to tell a story or describe a scene?","choices":["Abstract music","Absolute music","Programme music","Polyphonic music"],"explanation":"Programme music is instrumental music associated with a story, poem, or scene."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"How did the Romantic orchestra differ from the Classical orchestra?","choices":["It became much smaller","It removed the brass section","It became larger and added newer instruments","It only used string instruments"],"explanation":"The Romantic orchestra expanded significantly, adding instruments like the piccolo, tuba, and more percussion."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which technique involves subtle speeding up and slowing down of the tempo for expression?","choices":["Staccato","Rubato","Legato","Pizzicato"],"explanation":"Rubato (\"robbed time\") is a hallmark of Romantic performance, allowing for flexible expression."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Composers using folk dances and legends from their homeland are expressing:","choices":["Atheism","Serialism","Nationalism","Neo-classicism"],"explanation":"Musical nationalism was a major trend as composers sought to create a distinct identity for their countries."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which instrument underwent major mechanical improvements in the 19th century?","choices":["Harpsichord","Lute","Piano","Recorder"],"explanation":"The piano gained a cast-iron frame, more keys, and cross-stringing, allowing for greater power and range."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"Lied\" (plural \"Lieder\")?","choices":["A long orchestral work","A German art song for voice and piano","A type of opera","A religious chant"],"explanation":"Lieder are Romantic art songs where the piano and voice are equal partners in conveying the poem''s mood."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which composer is primary associated with virtuoso piano music?","choices":["J.S. Bach","Frédéric Chopin","Joseph Haydn","Antonio Vivaldi"],"explanation":"Chopin was the \"poet of the piano,\" known for его expressive and technically demanding piano works."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Chromaticism in Romantic music often led to:","choices":["Simpler harmonies","More predictable modulations","Increased dissonance and emotional tension","The end of major and minor keys"],"explanation":"Chromatic notes (outside the home key) added color, tension, and a sense of longing to the music."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which dynamic marking would be MORE common in the Romantic era than the Classical?","choices":["p","f","ffff","mf"],"explanation":"Romantic composers used extreme dynamic ranges, from ppp to ffff."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"A \"Symphonic Poem\" is usually:","choices":["A four-movement symphony","A single-movement orchestral work with a program","A solo piano piece","A choral work"],"explanation":"The symphonic poem (or tone poem) was pioneered by Lizst and refined by Smetana and Strauss."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Through-composed form in Lieder means:","choices":["The music stays the same for every verse","The music changes to follow the story of each verse","There is only one verse","The singer and pianist alternate"],"explanation":"\"Durchkomponiert\" means the music evolves with the text, never simply repeating a whole section."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which of these is a typical Romantic texture?","choices":["Thin, single-line monophony","Rich, layered homophony with pedal use on piano","Very strict 2-part counterpoint only","No texture at all"],"explanation":"Romantic textures are often dense, rich, and \"sonorous,\" especially with the use of the sustaining pedal."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which IGCSE Focus Work represents Romantic nationalism?","choices":["Vivaldi''s Spring","Haydn''s \"Joke\" Quartet","Smetana''s Vltava","Handel''s Messiah"],"explanation":"Smetana''s Vltava depicts the landscapes and legends of his beloved Bohemia."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The \"Virtuoso\" perfromer was often treated like:","choices":["A servant","A rock star or celebrity","A background noise","A teacher only"],"explanation":"Performers like Liszt and Paganini gained fame for their incredible technical skills."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What happened to melody in the Romantic era?","choices":["It became shorter and less important","It became longer, more lyrical and expansive","It was removed completely","It only used two notes"],"explanation":"Romantic melodies are known for their song-like (\"bel canto\") quality and long phrases."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which era came BEFORE the Romantic era?","choices":["Classical","Modern","Renaissance","Baroque"],"explanation":"The chronlogical order is Baroque -> Classical -> Romantic -> Modern."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Harmony in the 19th century became more:","choices":["Functional and simple","Complex, using distant modulations","Trained to only use C major","Silent"],"explanation":"Composers explored \"distant\" keys that sounded exotic or dramatic compared to the home key."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"True or False: The pedal is essential for Romantic piano music.","choices":["True","False"],"explanation":"The sustaining (damper) pedal creates the warm, \"ringing\" quality typical of Romantic piano style."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What does the German word \"Lied\" mean?","choices":["Light","Leader","Song","Life"],"explanation":"\"Lied\" (plural \"Lieder\") is the German word for song."}'::jsonb, '{"correct":2}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Who is considered the most prolific and famous composer of Lieder?","choices":["Franz Schubert","Richard Wagner","Johann Sebastian Bach","Igor Stravinsky"],"explanation":"Schubert wrote over 600 Lieder in his short life."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In true Romantic Lieder, the piano is:","choices":["Less important than the singer","An equal partner to the singer, helping tell the story","Not used at all","Only used for the introduction"],"explanation":"The piano part often uses \"word painting\" to reflect the poem''s mood and imagery."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What form uses the SAME music for every verse of the poem?","choices":["Through-composed","Strophic","Binary","Fugal"],"explanation":"Strophic form (like a modern hymn or pop song) repeats the music for each stanza."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What form uses NEW music for each verse to follow the story?","choices":["Strophic","Through-composed (Durchkomponiert)","Ternary","Ground Bass"],"explanation":"Through-composed songs allow the composer to react to every emotional shift in the text."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is a \"Song Cycle\"?","choices":["A song about a bicycle","A group of Lieder designed to be performed together, often telling a story","A song that repeats forever","A type of musical instrument"],"explanation":"Examples include Schubert''s \"Winterreise\" and Schumann''s \"Dichterliebe\"."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which language is used for IGCSE Romantic Lieder?","choices":["English","German","Italian","French"],"explanation":"The genre developed from German Romantic poetry."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The piano part in \"Der Erlkönig\" (The Erlking) depicts:","choices":["A calm river","A galloping horse","A bird singing","Silence"],"explanation":"Rapid triplets in the right hand mimic the pounding hooves of the horse."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which composer married Clara Wieck and wrote \"Year of Song\" in 1840?","choices":["Robert Schumann","Brahms","Liszt","Berlioz"],"explanation":"Schumann wrote nearly 140 songs in a single year inspired by his love for Clara."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"What is \"Word Painting\" (Madrigalism)?","choices":["Painting a picture of the singer","Writing music that literally reflects the meaning of the words","Singing without lyrics","Using very loud dynamics"],"explanation":"E.g., high notes for \"heaven\" or chromatic descents for \"sorrow\"."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"A \"Modified Strophic\" song is one where:","choices":["There are no verses","The music is mostly the same but varies for dramatic effect","The singer and pianist switch roles","The music is randomly generated"],"explanation":"It combines the repetition of strophic with some of the flexibility of through-composed."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Who wrote the poetry for many of Schubert''s songs (including \"Erlkönig\")?","choices":["William Shakespeare","Johann Wolfgang von Goethe","Victor Hugo","Mark Twain"],"explanation":"Goethe was the preeminent German Romantic poet."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Lieder are usually performed in:","choices":["Large football stadiums","Intimate settings like salons or small concert halls","Only in churches","Underwater"],"explanation":"Lieder are essentially \"chamber music,\" designed for close-up emotional sharing."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"The \"mood\" of a Lied is often set by:","choices":["The piano introduction","The applause from the audience","The color of the singer''s shoes","The price of the ticket"],"explanation":"The piano prelude establishes the tempo, key, and emotional atmosphere before the singer begins."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"Which of these is a typical Lieder topic?","choices":["Computer science","Unrequited love and nature","Cooking recipes","Industrial manufacturing"],"explanation":"Romantic poets and composers were obsessed with love, longing, and the great outdoors."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general'),
('mcq', '{"prompt":"In Schubert''s \"Die Forelle\" (The Trout), the piano mimics:","choices":["A bird flying","A fish leaping in water","A storm blowing","A clock ticking"],"explanation":"Sparkling ascending figures depict the trout swimming in a brook."}'::jsonb, '{"correct":1}'::jsonb, 1, 'general');
INSERT INTO rpg_questions (type, content, answer_key, difficulty, subject_tag)
VALUES
('mcq', '{"prompt":"True or False: Romantic Lieder can be for any voice type (Soprano, Tenor, etc.).","choices":["True","False"],"explanation":"Lieder are written for all voice ranges depending on the poem and composer''s intent."}'::jsonb, '{"correct":0}'::jsonb, 1, 'general'),
